select nombreOperacion from operacion o, norma n, pieza p where o.codigoOperacion=n.Operacion_codigoOperacion and p.codigoPieza=n.Pieza_codigoPieza and p.descripcionPieza="Lamina Bronce";
select tipoMantenimiento,fechaMantenimiento from equipo e, obreroauxiliar oa where e.nombreEquipo=oa.Equipo_nombreEquipo and e.modeloEquipo=oa.Equipo_modeloEquipo and e.nombreEquipo="Cortadora";
select distinct o.nombreObrero, o.numeroObrero from obrero o, obrerodirecto od, norma n, pieza p where p.codigoPieza = n.Pieza_codigoPieza and od.Norma_codigoNorma = n.codigoNorma and  od.Obrero_numeroObrero = o.numeroObrero;
select distinct p.descripcionPieza, op.nombreOperacion, o.nombreObrero from pieza p, operacion op, norma n, obrerodirecto od, obrero o where p.codigoPieza = n.Pieza_codigoPieza and n.Operacion_codigoOperacion = op.codigoOperacion and od.Obrero_numeroObrero = o.numeroObrero and o.nombreObrero = "Andres Andrade" and op.nombreOperacion = "Fresado";
select * from norma n, operacion o, pieza p, equipo e where n.Pieza_codigoPieza = p.codigoPieza and n.Operacion_codigoOperacion = o.codigoOperacion and n.Equipo_nombreEquipo = e.nombreEquipo and (p.descripcionPieza = "Lamina Plata" or p.descripcionPieza = "Lamina Bronce") and o.nombreOperacion = "Empacado" and e.nombreEquipo = "Empacadora";
